INSERT INTO `contenidos` VALUES ('231', 'The Apprentice: La historia de Trump', 'Un joven Donald Trump (Sebastian Stan), ansioso por hacerse un nombre como hambriento segundo hijo de una familia adinerada en el Nueva York de los años 70, cae bajo el hechizo de Roy Cohn (Jeremy Strong), el despiadado abogado que ayudaría a crear al Donald Trump que conocemos hoy. Cohn ve en Trump al protegido perfecto: alguien con una ambición desmedida, sed de éxito y dispuesto a hacer lo que haga falta para ganar.', '2024', 'https://repelishd.cam//uploads/mini/cuimage/ee/7930aa14e4eef5fb6921eb836410d2.jpg', 'https://lookaside.instagram.com/seo/google_widget/crawler/?media_id=3456432864873833504', '7.1', 'pelicula', '2025-04-01 23:03:20', '2025-04-01 23:35:40', 'https://repelishd.cam/ver-pelicula/16581-the-apprentice-la-historia-de-trump-online-espanol.html');
